def create_key(shape_id):
    parts = shape_id.split('..')

    if len(parts) != 2:
        return None

    prefix = parts[0]
    details = parts[1]

    #ignoring south lines here, only need north
    if details[0] != 'N':
        return None
    
    route = details[1:]
    tkey = f"Line-{prefix}-Route-{route}"
    return tkey

tkeys = {
    shape_id: create_key(shape_id)
    for shape_id in shape_ids
    if create_key(shape_id) is not None
}

for shape_id, tkey in tkeys.items():
    print(f"{shape_id} : {tkey}")